import importImages from "./ImportImages";

const accessory = importImages(
  require.context("../images/accessories", false, /\.png/)
);
const background = importImages(
  require.context("../images/background", false, /\.png/)
);
const head = importImages(require.context("../images/head", false, /\.png/));
const face = importImages(require.context("../images/face", false, /\.png/));

const accessoryImages = [
  "ghostAccessory.png",
  "pumpkinAccessory.png",
  "noneAccessory.png",
];
const backgroundImages = [
  "blackBackground.png",
  "pinkBackground.png",
];
const hairImages = [
  "default.png",
  "bang.png",
  "curls.png",
  "elegant.png",
  "quiff.png",
  "short.png",
];
const legImages = [
  "default.png",
  "bubble-tea.png",
  "cookie.png",
  "game-console.png",
  "tilt-backward.png",
  "tilt-forward.png",
];
const mouthImages = [
  "default.png",
  "astonished.png",
  "eating.png",
  "laugh.png",
  "tongue.png",
];
const neckImages = [
  "default.png",
  "bend-backward.png",
  "bend-forward.png",
  "thick.png",
];

const randomize = (max) => {
  return Math.floor(Math.random() * max);
};

/**
 * Returns an object of random alpaca parts
 */
const randomImageGenerator = () => {
  const alapcaParts = {};
  alapcaParts["accessory"] = accessory[accessoryImages[randomize(3)]];
  alapcaParts["background"] = background[backgroundImages[randomize(2)]];
  alapcaParts["head"] = ear[earImages[randomize(2)]];
  alapcaParts["face"] = ear[earImages[randomize(2)]];
  return alapcaParts;
};

export default randomImageGenerator;
